    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        // TODO add your handling code here:
        
        jLabel8.setText("Welcome Dear " + DAL.username);
        
        jLabel8.setForeground(Color.white);
        
        Color c = new Color(34, 102, 112);
        getContentPane().setBackground(c);
        
        jPanel1.setBackground(c);
        
        
        
        jLabel6.setIcon(new ImageIcon(getClass().getResource("piechart.png").getFile()));
        
        
        jLabel5.setIcon(new ImageIcon(getClass().getResource("security_key.png").getFile()));
        
        

        setExtendedState(javax.swing.JFrame.MAXIMIZED_BOTH);

        if (DAL.admin_level < 1) {
            jMenu1.setEnabled(false);
        }

        if (DAL.admin_level < 2) {
            jMenuItem1.setEnabled(false);
        }


        if (DAL.customer_level < 1) {
            jMenu2.setEnabled(false);
        }
        if (DAL.goods_level < 1) {
            jMenu3.setEnabled(false);
        }
        if (DAL.sales_level < 1) {
            jMenu4.setEnabled(false);
        }



        if (DAL.customer_level < 2) {
            jMenuItem3.setEnabled(false);
        }
        if (DAL.goods_level < 2) {
            jMenuItem5.setEnabled(false);
        }
        if (DAL.sales_level < 2) {
            jMenuItem7.setEnabled(false);
        }


        jInternalFrame1.setVisible(false);
        
        
        
        
        

    }//GEN-LAST:event_formWindowOpened

