    private void jPasswordField1KeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jPasswordField1KeyPressed
        // TODO add your handling code here:
        if (evt.getKeyCode() == 10) {

            String currentPass = Helper.Tools.convertPassword(jPasswordField1.getPassword());

            DAL d = new DAL();

            d.rs = d.executeQuery("SELECT * FROM users WHERE username = '" + DAL.username + "' AND password = PASSWORD('" + currentPass + "')");
            try {
                d.rs.next();
                int row = d.rs.getRow();
                
                if(row == 1) {
                    jPasswordField2.setEnabled(true);
                    jPasswordField3.setEnabled(true);
                }
                else {
                    Helper.Tools.show("Incorrect Password!");
                }
                
            } catch (Exception e) {
            }
        }
    }//GEN-LAST:event_jPasswordField1KeyPressed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        
        String newPass = Helper.Tools.convertPassword(jPasswordField2.getPassword());
        String confirm = Helper.Tools.convertPassword(jPasswordField3.getPassword());
        
        if(newPass.equals(confirm)) {
            DAL d = new DAL();
            boolean result = d.execute("UPDATE users SET password = PASSWORD('" + confirm + "') WHERE username = '" + DAL.username + "'");
            
            if(result) {
                Helper.Tools.show("Password Successfully Changed!");
                jInternalFrame1.setVisible(false);
            }
            else {
                Helper.Tools.show("OOPS! Password Not Changed!");
            }
            
        }
        else {
            Helper.Tools.show("Password Does Not Match!");
        }
        
    }//GEN-LAST:event_jButton2ActionPerformed

