    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        // TODO add your handling code here:


        Helper.Tools.setCenter(this);

        DAL d = new DAL();

        d.rs = d.executeQuery("SELECT username FROM users WHERE user_id=" + UserList.user_id);

        try {
            d.rs.next();

            jLabel1.setText(d.rs.getString("username") + "'s Permissions");

        } catch (Exception e) {
        }


        d.rs = d.executeQuery("SELECT * FROM user_level WHERE user_id=" + UserList.user_id);

        try {
            d.rs.next();
            
            int admin_level = d.rs.getInt("admin_level");
            int customer_level = d.rs.getInt("customer_level");
            int goods_level = d.rs.getInt("goods_level");
            int sales_level = d.rs.getInt("sales_level");
            
            if(admin_level == 0) {
                admin_none.setSelected(true);
            }
            else if(admin_level == 1) {
                admin_read.setSelected(true);
            }
            else if(admin_level == 2) {
                admin_add.setSelected(true);
            }
            else if(admin_level == 4) {
                admin_edit.setSelected(true);
            }
            else if(admin_level == 8) {
                admin_delete.setSelected(true);
            }
            
            
            if(goods_level == 0) {
                goods_none.setSelected(true);
            }
            else if(goods_level == 1) {
                goods_read.setSelected(true);
            }
            else if(goods_level == 2) {
                goods_add.setSelected(true);
            }
            else if(goods_level == 4) {
                goods_edit.setSelected(true);
            }
            else if(goods_level == 8) {
                goods_delete.setSelected(true);
            }
            
            
            if(customer_level == 0) {
                customer_none.setSelected(true);
            }
            else if(customer_level == 1) {
                customer_read.setSelected(true);
            }
            else if(customer_level == 2) {
                customer_add.setSelected(true);
            }
            else if(customer_level == 4) {
                customer_edit.setSelected(true);
            }
            else if(customer_level == 8) {
                customer_delete.setSelected(true);
            }
            
            
            if(sales_level == 0) {
                sales_none.setSelected(true);
            }
            else if(sales_level == 1) {
                sales_read.setSelected(true);
            }
            else if(sales_level == 2) {
                sales_add.setSelected(true);
            }
            else if(sales_level == 4) {
                sales_edit.setSelected(true);
            }
            else if(sales_level == 8) {
                sales_delete.setSelected(true);
            }
            
            
        } catch (Exception e) {
        }





    }//GEN-LAST:event_formWindowOpened

