    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        
        int admin_level = 0;
        int customer_level = 0;
        int goods_level = 0;
        int sales_level = 0;
        
        if(admin_read.isSelected()) {
            admin_level = 1;
        }
        else if(admin_add.isSelected()) {
            admin_level = 2;
        }
        else if(admin_edit.isSelected()) {
            admin_level = 4;
        }
        else if(admin_delete.isSelected()) {
            admin_level = 8;
        }
        
        if(customer_read.isSelected()) {
            customer_level = 1;
        }
        else if(customer_add.isSelected()) {
            customer_level = 2;
        }
        else if(customer_edit.isSelected()) {
            customer_level = 4;
        }
        else if(customer_delete.isSelected()) {
            customer_level = 8;
        }
        
        if(goods_read.isSelected()) {
            goods_level = 1;
        }
        else if(goods_add.isSelected()) {
            goods_level = 2;
        }
        else if(goods_edit.isSelected()) {
            goods_level = 4;
        }
        else if(goods_delete.isSelected()) {
            goods_level = 8;
        }
        
        if(sales_read.isSelected()) {
            sales_level = 1;
        }
        else if(sales_add.isSelected()) {
            sales_level = 2;
        }
        else if(sales_edit.isSelected()) {
            sales_level = 4;
        }
        else if(sales_delete.isSelected()) {
            sales_level = 8;
        }
        
        
        DAL d = new DAL();
        
        String command = "UPDATE user_level SET admin_level=%d, goods_level=%d, customer_level=%d, sales_level=%d WHERE user_id=%d";
 
        command = String.format(command, admin_level, goods_level, customer_level, sales_level, UserList.user_id);
        
        if(d.execute(command)) {
            Helper.Tools.show("Permission Successfully Updated!");
            this.setVisible(false);
        }
        else {
            Helper.Tools.show("Error! Please Try Again!");
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed
