    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        // TODO add your handling code here:
        
        if(DAL.admin_level < 8) {
            jButton1.setEnabled(false);
        }
        
        if(DAL.admin_level < 4) {
            jButton2.setEnabled(false);
        }
        
        if(DAL.admin_level < 8) {
            jButton6.setEnabled(false);
        }
        

        jPanel1.setVisible(false);
        this.setSize(this.getSize().width, this.getSize().height - 55);


        Helper.Tools.setCenter(this);

        DAL d = new DAL();

        Object[][] result = d.select("SELECT user_id, username FROM users");

        String[] title = {"ID", "Username"};

        jTable1 = new javax.swing.JTable(result, title);


        jScrollPane1.setViewportView(jTable1);






    }//GEN-LAST:event_formWindowOpened

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:

        if (jTable1.getSelectedRow() > -1) {

            Object username = jTable1.getValueAt(jTable1.getSelectedRow(), 1);

            int answer = Helper.Tools.confirm("Are You Sure To Delete: " + username + "?");

            if (answer == 0) {

                Object user_id = jTable1.getValueAt(jTable1.getSelectedRow(), 0);

                if (Integer.parseInt(user_id.toString()) != 1) {

                    DAL d = new DAL();

                    boolean result = d.execute("DELETE FROM users WHERE user_id =" + user_id);

                    if (result) {
                        formWindowOpened(null);
                        Helper.Tools.show("Sucessfully Deleted!");
                    } else {
                        Helper.Tools.show("Error! Try Again");
                    }
                } else {
                    Helper.Tools.show("Prohibited Operation on Admin User!");
                }
            }
        }


    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:        

        this.setSize(this.getSize().width, this.getSize().height + 55);
        jPanel1.setVisible(true);

        DAL d = new DAL();

        Object user_id = jTable1.getValueAt(jTable1.getSelectedRow(), 0);

        d.rs = d.executeQuery("SELECT username FROM users WHERE user_id=" + user_id);

        try {
            d.rs.next();
            jTextField2.setText(d.rs.getString("username"));
        } catch (Exception e) {
        }



    }//GEN-LAST:event_jButton2ActionPerformed

