    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:

        String newuser = jTextField2.getText();
        String newpass = Helper.Tools.convertPassword(jPasswordField1.getPassword());
        String confpass = Helper.Tools.convertPassword(jPasswordField2.getPassword());

        DAL d = new DAL();

        Object user_id = jTable1.getValueAt(jTable1.getSelectedRow(), 0);

        if (newpass.equals("")) {
            // query update username
            boolean result = d.execute("UPDATE users SET username = '" + newuser + "' WHERE user_id = " + user_id);
            if (result) {
                formWindowOpened(null);
                Helper.Tools.show("Successfully Changed!");
            } else {
                Helper.Tools.show("Error! Try Again!");
            }
        } else {
            if (newpass.equals(confpass)) {
                // query update username, password
                boolean result = d.execute("UPDATE users SET username = '" + newuser + "', password = PASSWORD('" + newpass + "') WHERE user_id =" + user_id);
                if (result) {
                    formWindowOpened(null);
                    Helper.Tools.show("Successfully Changed!");
                } else {
                    Helper.Tools.show("Error! Try Again!");
                }
            }
            else {
                Helper.Tools.show("Password Does Not Match!");
            }
        }
        
        

    }//GEN-LAST:event_jButton4ActionPerformed

    private void jButton6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton6ActionPerformed
        // TODO add your handling code here:
        user_id = jTable1.getValueAt(jTable1.getSelectedRow(), 0);
        
        if(Integer.parseInt(user_id.toString()) == 1) {
            Helper.Tools.show("Prohibited Operation on Admin User!");
        }
        else {
            new UserLevel().setVisible(true);
        }
    }//GEN-LAST:event_jButton6ActionPerformed

